def rle_encode(data):
    if not data:
        return []
        
    result = []
    i = 0
    length = len(data)
    
    while i < length:
        if data[i] != 0:  # 非0值直接存储
            result.append(data[i])
            i += 1
        else:  # 处理0值
            # 计算连续的0的数量
            count = 1
            j = i + 1
            while j < length and data[j] == 0:
                count += 1
                j += 1
            
            if count == 1:  # 单个0直接存储
                result.append(0)
            else:  # 多个0进行压缩
                result.append([count, 0])
            i = j
    
    return result


def to_file():
    resault= {}
    resault.update({'WIDTH':setting['width'],'HEIGHT':setting['height'],'DEFAULT':[],'rle': 1 if setting['rle'] else 0})

    for num,char in enumerate(string):
        cache = []
        for line in range(int( len(hex_list)/len(string)/2 ) ):
            cache += [hex_list[line*190+num*2],hex_list[line*190+num*2+1]]

        encode_cache=rle_encode(cache)
        resault.update({char:encode_cache})
    print('单个字符字节数',len(resault[' ']))
    import json 
    with open(f'./font_{setting['width']}x{setting['height']}{'_rle' if setting['rle'] else ''}.py',"w") as f:
        f.write('FontData=')
        json.dump(resault,f)

to_file()